const FAQItem = (props: { question: string; answer: string }) => {
  const [expanded, setExpanded] = useState(false);
  const { colors } = useThemeColors();
  return (
    <TouchableOpacity
      style={{
        padding: 16,
        backgroundColor: colors.secondary.background,
        borderRadius: 10,
        marginBottom: 10,
        gap: 12
      }}
      activeOpacity={0.9}
      onPress={() => {
        setExpanded(!expanded);
      }}
      key={props.question}
    >
      <View
        style={{
          flexDirection: "row",
          width: "100%",
          justifyContent: "space-between"
        }}
      >
        <Heading
          style={{
            flexShrink: 1
          }}
          size={SIZE.md}
        >
          {props.question}
        </Heading>
        <Icon
          name={expanded ? "chevron-up" : "chevron-down"}
          color={colors.secondary.icon}
          size={SIZE.xxl}
        />
      </View>
      {expanded ? <Paragraph size={SIZE.md}>{props.answer}</Paragraph> : null}
    </TouchableOpacity>
  );
};

const ComparePlans = React.memo(
  () => {
    const { colors } = useThemeColors();
    return (
      <ScrollView
        horizontal
        style={{
          width: "100%"
        }}
        contentContainerStyle={{
          flexDirection: "column"
        }}
      >
        {Object.keys(FeaturesList).map((key, keyIndex) => {
          return (
            <View
              key={key}
              style={{
                flexDirection: "row",
                alignItems: "center",
                width: "100%",
                gap: 10
              }}
            >
              {[
                key === "Plans" ? "" : key,
                ...FeaturesList[key as keyof typeof FeaturesList]
              ].map((item, index) => (
                <View
                  style={{
                    width: index === 0 ? 150 : 120,
                    paddingHorizontal: 16,
                    paddingVertical: 12,
                    backgroundColor:
                      index === 0 ? colors.secondary.background : undefined,
                    borderBottomWidth: index === 0 ? 1 : undefined,
                    borderBottomColor: colors.primary.border
                  }}
                  key={item + key + index}
                >
                  {item === true ? (
                    <Icon
                      color={colors.primary.accent}
                      size={SIZE.sm}
                      name="check"
                    />
                  ) : item === false ? (
                    <Icon
                      size={SIZE.sm}
                      color={colors.static.red}
                      name="close"
                    />
                  ) : keyIndex === 0 ? (
                    <Heading size={SIZE.sm}>{item}</Heading>
                  ) : (
                    <Paragraph>{item}</Paragraph>
                  )}
                </View>
              ))}
            </View>
          );
        })}
      </ScrollView>
    );
  },
  () => true
);
ComparePlans.displayName = "ComparePlans";

