const PricingPlanCard = ({
  plan,
  pricingPlans,
  annualBilling
}: {
  plan: PricingPlan;
  pricingPlans?: ReturnType<typeof usePricingPlans>;
  annualBilling?: boolean;
}) => {
  const { colors } = useThemeColors();
  const isSelected = pricingPlans?.currentPlan?.id === plan.id;
  const isFreePlan = plan.id === "free";

  const product =
    plan.products?.[
      `notesnook.${plan.id}.${annualBilling ? "yearly" : "monthly"}`
    ];

  const discountPercentage = annualBilling
    ? pricingPlans?.compareProductPrice(
        plan.id,
        `notesnook.${plan.id}.yearly`,
        `notesnook.${plan.id}.monthly`
      )
    : null;

  const price =
    Platform.OS === "android"
      ? pricingPlans?.getAndroidPrice(
          product as RNIap.SubscriptionAndroid,
          1,
          annualBilling
        )
      : null;
  console.log(product as RNIap.SubscriptionIOS);

  return (
    <TouchableOpacity
      activeOpacity={0.9}
      onPress={() => {
        pricingPlans?.selectPlan(plan.id);
      }}
      style={{
        ...getElevationStyle(3),
        backgroundColor: colors.primary.background,
        borderWidth: 1,
        borderColor: isSelected ? colors.primary.accent : colors.primary.border,
        borderRadius: 10,
        padding: 16,
        width: "100%",
        flexDirection: "column",
        justifyContent: "space-between",
        gap: 6
      }}
    >
      {discountPercentage ? (
        <View
          style={{
            backgroundColor: colors.static.red,
            borderRadius: 100,
            paddingHorizontal: 6,
            alignItems: "center",
            justifyContent: "center",
            height: 25,
            alignSelf: "flex-start"
          }}
        >
          <Heading size={SIZE.xs}>{discountPercentage}% Off</Heading>
        </View>
      ) : null}

      {/* {plan.recommended ? (
        <View
          style={{
            backgroundColor: colors.static.red,
            borderRadius: 100,
            paddingHorizontal: 6,
            paddingVertical: 2,
            position: "absolute",
            bottom: 10,
            right: 10
          }}
        >
          <Paragraph
            style={{
              fontSize: SIZE.xs
            }}
            color={colors.static.white}
          >
            Recommended
          </Paragraph>
        </View>
      ) : null} */}

      <View>
        <Heading size={SIZE.md}>
          {plan.name}{" "}
          {plan.recommended ? (
            <Text
              style={{
                color: colors.primary.accent,
                fontSize: 12
              }}
            >
              (Recommended)
            </Text>
          ) : null}
        </Heading>
        <Paragraph>{plan.description}</Paragraph>
      </View>

      {pricingPlans?.loading ? (
        <ActivityIndicator size="small" color={colors.primary.accent} />
      ) : (
        <View>
          <Paragraph size={SIZE.lg}>
            {isFreePlan ? "0.00" : price} <Paragraph>/month</Paragraph>
          </Paragraph>

          {isFreePlan ? null : (
            <Paragraph color={colors.secondary.paragraph} size={SIZE.xs}>
              billed {annualBilling ? "annually" : "monthly"} at{" "}
              {pricingPlans?.getStandardPrice(product as RNIap.Subscription)}{" "}
            </Paragraph>
          )}
        </View>
      )}
    </TouchableOpacity>
  );
};
